private async checkRelevance(text: string): Promise<number> {
    try {
        elizaLogger.info("[SttTtsPlugin] Checking relevance for text:", {
            text,
            length: text.length
        });

        // Respond to very short messages if they might be addressing the agent
        if (text.length < 5) {
            // Check if it sounds like "hey", "hi", "yo" etc
            if (/^(hey|hi|yo|oh|um|uh|you|byte)\b/i.test(text)) {
                elizaLogger.info("[SttTtsPlugin] Detected greeting/acknowledgment:", text);
                return 0.7; // 70% base score for greetings
            }
        }

        // Always respond to questions or direct address
        const isQuestion = text.includes('?') || /^(what|who|where|when|why|how|can|could|would|will|do|does|did|is|are|was|were)\b/i.test(text);
        const isDirectAddress = this.agentName ? text.toLowerCase().includes(this.agentName.toLowerCase()) : false;

        if (isQuestion || isDirectAddress) {
            elizaLogger.info(`[SttTtsPlugin] High priority message detected:`, {
                text,
                isQuestion,
                isDirectAddress,
                agentName: this.agentName
            });
            return 0.9; // 90% base score for questions/direct address
        }

        // Get relevant knowledge with more detailed logging
        elizaLogger.info("[SttTtsPlugin] Querying RAG knowledge for:", text);
        const knowledgeData = await this.runtime.ragKnowledgeManager.getKnowledge({
            query: text,
            limit: 3
        }).catch(err => {
            elizaLogger.error("[SttTtsPlugin] Error getting knowledge:", err);
            return [];
        });

        // Log retrieved knowledge
        knowledgeData.forEach((k, index) => {
            elizaLogger.info(`[SttTtsPlugin] RAG Knowledge ${index + 1}:`, {
                text: k?.content?.text,
                score: k?.score,
                source: k?.source
            });
        });

        // Calculate relevance score with detailed logging
        let relevanceScore = 0;
        const scoreDetails: string[] = [];

        // Base score for any non-empty message
        if (text.length > 0) {
            relevanceScore += 0.2;
            scoreDetails.push("+0.2 (non-empty message)");
        }

        knowledgeData.forEach((k, index) => {
            if (!k?.content?.text) return;

            // Check for exact phrase matches
            if (k.content.text.toLowerCase().includes(text.toLowerCase())) {
                relevanceScore += 0.4;
                scoreDetails.push(`+0.4 (exact match in knowledge ${index + 1})`);
            }

            // Add base score for relevant knowledge
            relevanceScore += 0.2;
            scoreDetails.push(`+0.2 (relevant knowledge ${index + 1})`);
        });

        // Log final relevance calculation
        elizaLogger.info("[SttTtsPlugin] Relevance calculation complete:", {
            inputText: text,
            finalScore: relevanceScore,
            scoreBreakdown: scoreDetails,
            knowledgeCount: knowledgeData.length
        });

        return relevanceScore;
    } catch (error) {
        elizaLogger.error("[SttTtsPlugin] Error in checkRelevance:", error);
        return 0.5; // Default to 50% relevance on error
    }
}

