export class SwapAction {
    constructor(private walletProvider: WalletProvider) {}

    async swapSingle(params: SwapParams): Promise<SwapResponse | string> {
        elizaLogger.info("Single swap params same chain:", params);
        const fromAddress = this.walletProvider.getAddress();

        const requiredFields = await this.checkRequiredParams(params);

        if (requiredFields.length > 0) {
            return requiredFields;
        }

        const requiredParams = await this.validateParams(params)

        if (requiredParams.length > 0) {
            return requiredParams;
        }

        const ponderProvider = new PonderAPIProvider();
        const chains = await ponderProvider.getChains();

        const blockExplorer = chains.find((chain) => chain.name.toLowerCase() === params.chain.toLowerCase())?.blockExplorer;

        const inputChainId = String(await ponderProvider.getChainId(params.chain, chains));
        const outputChainId = String(await ponderProvider.getChainId(params.chain, chains));

        const resp: SwapResponse = {
           fromChain: params.chain as SupportedChain,
           toChain: params.chain as SupportedChain,
           fromTokenSymbol: params.inputTokenSymbol,
           toTokenSymbol: params.outputTokenSymbol,
           fromToken: params.inputToken,
           toToken: params.outputToken,
           txHash: blockExplorer + "tx/",
           recipient:fromAddress,
           fromAmount: params.amount,
           toAmount: ""
        }

        const publicClient = this.walletProvider.getPublicClient(
            params.chain
        );

        let decimals;

        if (params.inputToken === zeroAddress) {
            decimals = 18;
        } else {
            decimals = await publicClient.readContract({
                address: params.inputToken as `0x${string}`,
                abi: erc20Abi,
                functionName: "decimals",
            });
        }

        params.amount = parseUnits(params.amount, decimals).toString();

        this.walletProvider.switchChain(params.chain);

        const quotePayload: QuotePayload = {
            fromChain: inputChainId,
            toChain: outputChainId,
            fromToken: [params.inputToken],
            toToken: [params.outputToken],
            fromAmount: [params.amount],
            slippage: 0.5, //TODO: slippage fix now returning "null" in string form
            fromAddress: fromAddress,
            toAddress: fromAddress,
            provider: ""
        }

        const quote = await ponderProvider.getQuote(quotePayload);

        if (!quote) {
            throw new Error("No quote found for the given input");
        }

        const routePayload: RoutePayload = {
            fromChain: inputChainId,
            toChain: outputChainId,
            fromToken: [params.inputToken],
            toToken: [params.outputToken],
            fromAmount: [params.amount],
            slippage: params.slippage,
            fromAddress: fromAddress,
            provider: quote.protocol,
            routeId: quote.routeId
        }

        let route = await ponderProvider.getRoute(routePayload);

        if (!route) {
            throw new Error("No route found for the given quote");
        }

        if (route.approvalTx) {
            const approveAmount = BigInt(params.amount)
            const token = await this.walletProvider.formatAddress(params.inputToken)

            const { args } = decodeFunctionData({
                abi: erc20Abi,
                data: route.approvalTx[0].data
              })

            const spender = await this.walletProvider.formatAddress(args[0] as string)

            const allowance = await this.walletProvider.checkERC20Allowance(
                params.chain,
                token,
                fromAddress,
                spender
            );
            if (allowance < approveAmount) {
                elizaLogger.info(
                    `Increasing ERC20 token ${params.inputTokenSymbol} allowance for ${route.protocol}. ${approveAmount - allowance} more needed`
                );
                const txHash = await this.walletProvider.approveERC20(
                    params.chain,
                    token,
                    spender,
                    approveAmount
                );
                await publicClient.waitForTransactionReceipt({
                    hash: txHash,
                });

                elizaLogger.info(`successfully approved ${params.inputTokenSymbol} for swap`);
            }
        }

        route = await ponderProvider.getRoute(routePayload);

        if (!route.executionTx) {
            throw new Error("No execution data found for the given route");
        }

        const options: { gas?: bigint; gasPrice?: bigint; data?: Hex } = {}

        options.gas = BigInt(route.executionTx.gasLimit);
        options.gasPrice = BigInt(route.executionTx.gasPrice);
        options.data = route.executionTx.data;

        const txHash = await this.walletProvider.swapToken(params.chain, route.executionTx.to, BigInt(route.executionTx.value), options)

        if (!txHash || txHash === "0x") {
            throw new Error("Get transaction hash failed");
        }

        // wait for the transaction to be confirmed
        await publicClient.waitForTransactionReceipt({
            hash: txHash,
        });

        const toAmount = formatUnits(quote.transactionDetails[0].toToken[0].toAmount,  quote.transactionDetails[0].toToken[0].decimals);

        resp.toAmount = toAmount.toString(); 
        resp.txHash = resp.txHash + txHash;

        return resp;

    }

