        if (!currentState) {
            currentState = (await runtime.composeState(message)) as State;
        } else {
            currentState = await runtime.updateRecentMessageState(currentState);
        }
        state.walletInfo = await evmWalletProvider.get(
            runtime,
            message,
            currentState
        );

        // Compose swap context
        const swapContext = composeContext({
            state: currentState,
            template: swapTemplate,
        });
        const content = await generateObjectDeprecated({
            runtime,
            context: swapContext,
            modelClass: ModelClass.LARGE,
        });

        elizaLogger.debug("Swap evm single token on same chain content:", content);

        const slippage = (!content?.slippage || content.slippage === null || content.slippage === "null") ? 0.5 : Number(content.slippage);

        const walletProvider = initWalletProvider(runtime);
        const action = new SwapAction(walletProvider);
        const swapParams: SwapParams = {
            inputTokenSymbol: content.inputTokenSymbol,
            outputTokenSymbol: content.outputTokenSymbol,
            inputToken: content.inputToken,
            outputToken: content.outputToken,
            amount: content.amount,
            chain: content.chain,
            slippage: slippage,
        };
        try {
            const swapResp = await action.swapSingle(swapParams);

            if (typeof swapResp === "string") {
                callback?.({
                    text: `You are trying to do token swap on same evm chain, but following fields are missing or not correct:\n${swapResp}`,
                    content: { error: swapResp }
                });
                return false;
            }

            callback?.({
                text: `Successfully swapped ${swapResp.fromAmount} ${swapResp.fromTokenSymbol} to ${swapResp.toAmount} ${swapResp.toTokenSymbol} to receipent address ${swapResp.recipient} on ${swapResp.toChain} chain\nTransaction Hash: ${swapResp.txHash}`,
                content: { ...swapResp },
            });

            return true;
        } catch (error) {
            elizaLogger.error("Error during single token swap:", error.message);
            callback?.({
                text: `Swap failed: ${error.message}`,
                content: { error: error.message }
            });
            return false;
        }
    },
    template: swapTemplate,

    validate: async (runtime: IAgentRuntime) => {
        const privateKey = runtime.getSetting("WALLET_PRIVATE_KEY");
        return typeof privateKey === "string" && privateKey.startsWith("0x");
    },
    examples: swapExamplesSingleToken,
    similes: ["SWAP", "TOKEN_SWAP", "SWAP_TOKEN", "SWAP_FUND", "CONVERT", "EXCHANGE_TOKEN", "TOKEN_CONVERSION"]

};


